pub use worker_download_and_build::BuildWorker;
pub use worker_patch::PatchWorker;

/// A Playbook is a sequence of numbered tasks the orchestrator will execute.
#[derive(Debug, Serialize, Deserialize, Clone)]
pub struct Playbook {
    pub name: String,
    pub tasks: Vec<Task>,
}

/// A single task is a small independent unit that a worker will run.
#[derive(Debug, Serialize, Deserialize, Clone)]
pub struct Task {
    pub id: usize,
    /// Human readable description.
    pub description: String,
    /// Payload for the worker, serialised as a map.
    #[serde(flatten)]
    pub payload: serde_json::Value,
}

/// Result returned by a worker after completing a task.
#[derive(Debug, Serialize, Deserialize, Clone)]
pub struct TaskResult {
    pub task_id: usize,
    pub success: bool,
    pub output: serde_json::Value,
}

/// Workers implement this trait.  The orchestrator will call `run` for every
/// task in a playbook.
#[async_trait]
pub trait TaskWorker {
    /// Execute one `task` and return the structured result.
    async fn run(&self, task: Task) -> Result<TaskResult>;

    /// Name used for explicit routing (e.g., "ocodex", "builder", "demo").
    fn name(&self) -> &'static str;

    /// Optional capability check for implicit routing. Default: false.
    fn can_handle(&self, _task: &Task) -> bool { false }
}

/// Simple single-worker orchestrator (kept for compatibility and tests).
pub struct Orchestrator<W: TaskWorker + Sync + Send> {
    worker: Arc<W>,
}

impl<W: TaskWorker + Sync + Send> Orchestrator<W> {
    pub fn new(worker: Arc<W>) -> Self { Self { worker } }

    /// Execute all tasks in the playbook sequentially.
    pub async fn execute(&self, playbook: Playbook) -> Result<Vec<TaskResult>> {
        let mut results = Vec::new();
        for task in playbook.tasks {
            let res = self.worker.run(task.clone()).await?;
            results.push(res);
        }
        Ok(results)
    }
}

/// Multi-agent orchestrator with a planner and worker pool.
pub struct MultiAgentOrchestrator {
    planner: Arc<dyn Planner + Send + Sync>,
    workers: Vec<Arc<dyn TaskWorker + Send + Sync>>, // heterogeneous pool
    max_concurrency: usize,
}

impl MultiAgentOrchestrator {
    pub fn new(
        planner: Arc<dyn Planner + Send + Sync>,
        workers: Vec<Arc<dyn TaskWorker + Send + Sync>>,
        max_concurrency: usize,
    ) -> Self {
        let max_concurrency = max_concurrency.max(1);
        Self { planner, workers, max_concurrency }
    }

    /// Plan from a prompt, then execute with delegation and concurrency.
    pub async fn orchestrate_prompt(&self, prompt: &str) -> Result<Vec<TaskResult>> {
        let playbook = self.planner.plan_from_prompt(prompt).await?;
        self.execute_with_delegation(playbook).await
    }

    /// Execute a playbook concurrently and allow workers to delegate new tasks.
    pub async fn execute_with_delegation(&self, playbook: Playbook) -> Result<Vec<TaskResult>> {
        let mut results: Vec<TaskResult> = Vec::new();
        let mut queue: VecDeque<Task> = playbook.tasks.into();
        let mut next_id: usize = queue.back().map(|t| t.id + 1).unwrap_or(1);
        let mut in_flight: Vec<(bool, tokio::task::JoinHandle<(usize, anyhow::Result<TaskResult>)>)> = Vec::new();
        let mut rr_index = 0usize;
        let mut writes_in_flight = 0usize;

        while !queue.is_empty() || !in_flight.is_empty() {
            // Launch up to max_concurrency tasks
            while in_flight.len() < self.max_concurrency && !queue.is_empty() {
                // Find next eligible task respecting write lock
                let mut pick_index: Option<usize> = None;
                for (i, t) in queue.iter().enumerate() {
                    let needs_write = t.payload.get("needs_write_lock").and_then(|v| v.as_bool()).unwrap_or(false);
                    if needs_write && writes_in_flight > 0 { continue; }
                    pick_index = Some(i);
                    break;
                }
                if pick_index.is_none() { break; }
                let task = queue.remove(pick_index.unwrap()).unwrap();
                let needs_write = task.payload.get("needs_write_lock").and_then(|v| v.as_bool()).unwrap_or(false);
                let (worker, new_rr) = self.select_worker(&task, rr_index);
                rr_index = new_rr;
                crate::debug::log_event(
                    "task_launch",
                    serde_json::json!({
                        "task_id": task.id,
                        "description": task.description,
                        "payload": task.payload,
                        "worker": worker.name(),
                    }),
                );
                let handle = tokio::spawn(async move {
                    let id = task.id;
                    (id, worker.run(task).await)
                });
                if needs_write { writes_in_flight += 1; }
                in_flight.push((needs_write, handle));
            }

            // Await one task completion
            let (was_write, handle) = in_flight.remove(0);
            let (_id, res) = handle.await.expect("worker task panicked");
            if was_write && writes_in_flight > 0 { writes_in_flight -= 1; }
            match res {
                Ok(tr) => {
                    crate::debug::log_event(
                        "task_complete",
                        serde_json::json!({
                            "task_id": tr.task_id,
                            "success": tr.success,
                            "output_keys": tr.output.as_object().map(|m| m.keys().cloned().collect::<Vec<_>>()).unwrap_or_default(),
                        }),
                    );
                    // Detect delegation requests from worker output: { "delegate": [ {description, payload}, ... ] }
                    if let Some(delegates) = tr.output.get("delegate").and_then(|v| v.as_array()) {
                        for d in delegates {
                            let description = d
                                .get("description")
                                .and_then(|x| x.as_str())
                                .unwrap_or("delegated task")
                                .to_string();
                            let payload = d
                                .get("payload")
                                .cloned()
                                .unwrap_or_else(|| serde_json::json!({}));
                            queue.push_back(Task { id: next_id, description, payload });
                            crate::debug::log_event(
                                "task_enqueue_delegated",
                                serde_json::json!({"task_id": next_id - 1, "new_task_id": next_id}),
                            );
                            next_id += 1;
                        }
                    }
                    results.push(tr);
                }
                Err(e) => {
                    crate::debug::log_event(
                        "task_error",
                        serde_json::json!({"error": e.to_string()}),
                    );
                    results.push(TaskResult {
                        task_id: 0,
                        success: false,
                        output: serde_json::json!({
                            "error": e.to_string(),
                        }),
                    });
                }
            }
        }

        Ok(results)
    }

    fn select_worker(&self, task: &Task, rr_index: usize) -> (Arc<dyn TaskWorker + Send + Sync>, usize) {
        // 1) Explicit routing by payload.worker
