{"output":"use crate::{Playbook, Task};
use anyhow::{anyhow, Result};
use async_trait::async_trait;
use serde::{Deserialize, Serialize};
use serde_json::json;

#[async_trait]
pub trait LlmClient: Send + Sync {
    async fn chat_json(&self, system: &str, user: &str) -> Result<String>;
}

pub struct HttpLlmClient {
    pub base_url: String, // OpenAI-compatible /v1 endpoint
    pub model: String,
    pub api_key: Option<String>, // optional for local/oss
}

#[derive(Serialize)]
struct ChatRequest<'a> {
    model: &'a str,
    messages: Vec<ChatMessage<'a>>,
    temperature: f32,
}

#[derive(Serialize)]
struct ChatMessage<'a> {
    role: &'a str,
    content: &'a str,
}

#[derive(Deserialize)]
struct ChatResponse {
    choices: Vec<Choice>,
}

#[derive(Deserialize)]
struct Choice { message: ChoiceMessage }

#[derive(Deserialize)]
struct ChoiceMessage { content: String }

#[async_trait]
impl LlmClient for HttpLlmClient {
    async fn chat_json(&self, system: &str, user: &str) -> Result<String> {
        let url = format!("{}/chat/completions", self.base_url.trim_end_matches('/'));
        let req = ChatRequest {
            model: &self.model,
            messages: vec![
                ChatMessage { role: "system", content: system },
                ChatMessage { role: "user", content: user },
            ],
            temperature: 0.2,
        };
        let client = reqwest::Client::new();
        let mut b = client.post(url).json(&req);
        if let Some(k) = &self.api_key {
            b = b.bearer_auth(k);
        }
        let resp = b.send().await?.error_for_status()?;
        let data: ChatResponse = resp.json().await?;
        let content = data.choices.get(0)
            .ok_or_else(|| anyhow!("no choices"))?
            .message.content.clone();
        Ok(content)
    }
}

pub struct OllamaCliClient {
    pub model: String,
}

#[async_trait]
impl LlmClient for OllamaCliClient {
    async fn chat_json(&self, system: &str, user: &str) -> Result<String> {
        use tokio::io::{AsyncBufReadExt, BufReader};
        use tokio::process::Command;
        let prompt = format!("System:{}\n\nUser:{}\n\nAssistant:", system, user);
        let mut child = Command::new("ollama")
            .arg("run")
            .arg(&self.model)
            .arg(prompt)
            .stdout(std::process::Stdio::piped())
            .stderr(std::process::Stdio::piped())
            .spawn()?;
        let mut out = String::new();
        if let Some(stdout) = child.stdout.take() {
            let mut lines = BufReader::new(stdout).lines();
            while let Ok(Some(line)) = lines.next_line().await { out.push_str(&line); out.push('\n'); }
        }
        let _ = child.wait().await;
        Ok(out)
    }
}

pub struct LlmPlanner {
    client: Box<dyn LlmClient>,
}

impl LlmPlanner {
    pub fn from_env(model_override: Option<String>, force_cli: bool) -> Self {
        let model = model_override
            .or_else(|| std::env::var("ORCH_LLM_MODEL").ok())
            .unwrap_or_else(|| "gpt-oss:20b".to_string());
