#[async_trait]
impl crate::planner::Planner for LlmPlanner {
    async fn plan_from_prompt(&self, prompt: &str) -> Result<Playbook> {
        let system = "You are an orchestrator architect. Plan a sequence of tasks for a worker pool. Output ONLY minified JSON matching this schema: {\"name\": string, \"tasks\": [{\"id\": number, \"description\": string, \"worker\": string, \"payload\": object}] }. The \"worker\" field routes tasks (e.g., 'ocodex', 'demo', 'builder'). For each task, include \"payload\" to pass to that worker. Do not include comments, markdown, or extra text.";
        let user = format!("Goal: {}", prompt);
        let raw = self.client.chat_json(system, &user).await?;
        let json_text = extract_json(&raw).ok_or_else(|| anyhow!("LLM did not return JSON"))?;
        let v: serde_json::Value = serde_json::from_str(&json_text)?;
        // Convert to Playbook
        let name = v.get("name").and_then(|x| x.as_str()).unwrap_or("plan");
        let mut tasks_out: Vec<Task> = Vec::new();
        if let Some(tasks) = v.get("tasks").and_then(|x| x.as_array()) {
            for t in tasks {
                let id = t.get("id").and_then(|x| x.as_u64()).unwrap_or(1) as usize;
                let description = t.get("description").and_then(|x| x.as_str()).unwrap_or("").to_string();
                let worker = t.get("worker").and_then(|x| x.as_str()).unwrap_or("ocodex");
                let payload_obj = t.get("payload").cloned().unwrap_or_else(|| json!({}));
                // Flatten worker into payload for current orchestrator routing
                let mut payload = payload_obj;
                if let Some(obj) = payload.as_object_mut() {
                    obj.insert("worker".into(), json!(worker));
                }
                tasks_out.push(Task { id, description, payload });
            }
        }
        if tasks_out.is_empty() {
            // Fallback to single step
            tasks_out.push(Task { id: 1, description: "delegate prompt to ocodex".into(), payload: json!({"worker":"ocodex","prompt":prompt}) });
        }
        Ok(Playbook { name: name.to_string(), tasks: tasks_out })
    }
}

fn extract_json(s: &str) -> Option<String> {
    // Try to find the first balanced top-level JSON object
    let bytes = s.as_bytes();
    let mut depth = 0i32;
    let mut start: Option<usize> = None;
    for (i, &b) in bytes.iter().enumerate() {
        if b == b'{' {
            if depth == 0 { start = Some(i); }
            depth += 1;
        } else if b == b'}' {
            depth -= 1;
            if depth == 0 {
                if let Some(st) = start { return Some(s[st..=i].to_string()); }
            }
        }
    }
    None
}
