use anyhow::{anyhow, Result};
use async_trait::async_trait;
use serde_json::json;
use std::process::Stdio;
use tokio::io::AsyncWriteExt;
use tokio::process::Command;

/// Worker that applies unified diff patches to the current workspace.
/// Payload example:
/// { "worker": "patch", "action": "apply_patch", "patch": "...diff...", "tool": "git|patch", "cwd": "/work" }
pub struct PatchWorker;

#[async_trait]
impl TaskWorker for PatchWorker {
    fn name(&self) -> &'static str { "patch" }
    fn can_handle(&self, task: &Task) -> bool {
        task.payload.get("worker").and_then(|v| v.as_str()) == Some("patch") ||
        task.payload.get("action").and_then(|v| v.as_str()) == Some("apply_patch")
    }

    async fn run(&self, task: Task) -> Result<TaskResult> {
        let action = task.payload.get("action").and_then(|v| v.as_str()).unwrap_or("");
        if action != "apply_patch" { return Err(anyhow!("unsupported action")); }
        let patch = task.payload.get("patch").and_then(|v| v.as_str()).ok_or_else(|| anyhow!("missing patch"))?;
        let tool = task.payload.get("tool").and_then(|v| v.as_str()).unwrap_or("auto");
        let cwd = task.payload.get("cwd").and_then(|v| v.as_str());

        // Decide tool: prefer git if repo or explicitly requested
        let use_git = match tool {
            "git" => true,
            "patch" => false,
            _ => std::path::Path::new(".git").exists(),
        };

        if use_git {
            let mut cmd = Command::new("git");
            cmd.arg("apply").arg("-p0").arg("--whitespace=nowarn");
            if let Some(dir) = cwd { cmd.current_dir(dir); }
            cmd.stdin(Stdio::piped()).stdout(Stdio::piped()).stderr(Stdio::piped());
            let mut child = cmd.spawn()?;
            if let Some(mut sin) = child.stdin.take() { sin.write_all(patch.as_bytes()).await?; }
            let out = child.wait_with_output().await?;
            let success = out.status.success();
            return Ok(TaskResult {
                task_id: task.id,
                success,
                output: json!({
                    "tool": "git",
                    "status": out.status.code(),
                    "stdout": String::from_utf8_lossy(&out.stdout).to_string(),
                    "stderr": String::from_utf8_lossy(&out.stderr).to_string(),
                }),
            });
        } else {
            let mut cmd = Command::new("patch");
            cmd.arg("-p0").arg("-t").arg("-N");
            if let Some(dir) = cwd { cmd.current_dir(dir); }
            cmd.stdin(Stdio::piped()).stdout(Stdio::piped()).stderr(Stdio::piped());
            let mut child = cmd.spawn()?;
            if let Some(mut sin) = child.stdin.take() { sin.write_all(patch.as_bytes()).await?; }
            let out = child.wait_with_output().await?;
            let success = out.status.success();
            return Ok(TaskResult {
                task_id: task.id,
                success,
                output: json!({
                    "tool": "patch",
                    "status": out.status.code(),
                    "stdout": String::from_utf8_lossy(&out.stdout).to_string(),
                    "stderr": String::from_utf8_lossy(&out.stderr).to_string(),
                }),
            });
        }
    }
}

