        } else { false }
    }
    async fn run(&self, task: Task) -> Result<TaskResult> {
        // Simulate different actions with deterministic logic.
        let action = task
            .payload
            .get("action")
            .and_then(|v| v.as_str())
            .unwrap_or("echo");

        let output: Value = match action {
            "echo" => {
                let msg = task
                    .payload
                    .get("msg")
                    .and_then(|v| v.as_str())
                    .unwrap_or("");
                json!({"result": msg})
            }
            "add" => {
                let x = task.payload.get("x").and_then(|v| v.as_i64()).unwrap_or(0);
                let y = task.payload.get("y").and_then(|v| v.as_i64()).unwrap_or(0);
                json!({"result": x + y})
            }
            "scaffold" => {
                // Heuristically scaffold a Python number guessing game into Apps/.
                // Create Apps dir if missing, write a script file.
                let root = std::env::current_dir().unwrap_or_else(|_| PathBuf::from("."));
                let apps_dir = root.join("Apps");
                fs::create_dir_all(&apps_dir)?;
                let file_path = apps_dir.join("guess_number.py");
                let script = python_guess_game();
                fs::write(&file_path, script)?;
                json!({
                    "created_dir": apps_dir.to_string_lossy(),
                    "created_file": file_path.to_string_lossy(),
                    "note": "Python number guessing game scaffolded",
                })
            }
            "delegate" => {
                // Produce delegated subtasks that the orchestrator will enqueue.
                json!({
                    "delegate": [
                        {"description": "echo delegated", "payload": {"action": "echo", "msg": "from delegate"}},
                        {"description": "compute delegated", "payload": {"action": "add", "x": 1, "y": 2}},
                    ]
                })
            }
            _ => json!({"info": "unknown action", "payload": task.payload}),
        };

        Ok(TaskResult { task_id: task.id, success: true, output })
    }
}

fn python_guess_game() -> &'static str {
    r#"#!/usr/bin/env python3
import random

def main():
    print("Welcome to the Number Guessing Game!")
    low, high = 1, 100
    secret = random.randint(low, high)
    attempts = 0
    while True:
        try:
            guess = input(f"Guess a number between {low} and {high}: ")
            guess = int(guess.strip())
        except ValueError:
            print("Please enter a valid integer.")
            continue
        attempts += 1
        if guess < secret:
            print("Too low!")
        elif guess > secret:
            print("Too high!")
        else:
            print(f"Correct! The number was {secret}. Attempts: {attempts}")
            break

if __name__ == "__main__":
    main()
"#
}
