use std::io::Write;
use std::sync::OnceLock;

fn debug_file_path() -> Option<&'static str> {
    static PATH: OnceLock<Option<String>> = OnceLock::new();
    let v = PATH.get_or_init(|| std::env::var("ORCH_DEBUG_FILE").ok());
    v.as_deref()
}

fn is_stderr_echo() -> bool {
    std::env::var("ORCH_DEBUG_STDERR")
        .map(|v| v != "0" && v.to_lowercase() != "false")
        .unwrap_or(true)
}

pub fn log_event(kind: &str, payload: serde_json::Value) {
    let now = chrono::Utc::now().to_rfc3339_opts(chrono::SecondsFormat::Millis, true);
    let line = serde_json::json!({
        "ts": now,
        "event": kind,
        "data": payload,
    }).to_string();

    if let Some(path) = debug_file_path() {
        if let Ok(mut f) = OpenOptions::new().create(true).append(true).open(path) {
            let _ = writeln!(f, "{}", line);
        }
    }

    if is_stderr_echo() {
        eprintln!("[orchestrator] {}", line);
    }
}

