impl OcodexWorker {
    pub fn new() -> Self {
        // Use OCODEX_BIN if provided, otherwise rely on PATH with program name "ocodex".
        let bin = std::env::var("OCODEX_BIN").unwrap_or_else(|_| "ocodex".to_string());
        Self { bin }
    }
}

#[async_trait]
impl TaskWorker for OcodexWorker {
    fn name(&self) -> &'static str { "ocodex" }
    fn can_handle(&self, task: &crate::Task) -> bool {
        // Handle if explicitly targeted or if payload suggests ocodex args/prompt
        if let Some(w) = task.payload.get("worker").and_then(|v| v.as_str()) {
            if w == "ocodex" { return true; }
        }
        task.payload.get("args").is_some() || task.payload.get("prompt").is_some()
    }
    async fn run(&self, task: Task) -> Result<TaskResult> {
        // Payload may contain "args": [..] or "prompt": "..." plus optional extra args.
        let env_extra = std::env::var("OCODEX_EXTRA_ARGS").unwrap_or_default();
        let mut extra_args: Vec<String> = if env_extra.is_empty() {
            vec![]
        } else {
            shell_words::split(&env_extra).unwrap_or_else(|_| vec![])
        };
        if let Some(arr) = task.payload.get("extra_args").and_then(|v| v.as_array()) {
            for a in arr.iter().filter_map(|x| x.as_str()) { extra_args.push(a.to_string()); }
        }

        let interactive = task.payload.get("interactive").and_then(|v| v.as_bool()).unwrap_or(false);
        // Optional worker role designation for logging/UX (e.g., programmer, orchestrator)
