            .unwrap_or_else(|| "programmer".to_string());
        let args: Vec<String> = if let Some(arr) = task.payload.get("args").and_then(|v| v.as_array()) {
            arr.iter().filter_map(|x| x.as_str()).map(String::from).collect::<Vec<_>>()
        } else if let Some(p) = task.payload.get("prompt").and_then(|v| v.as_str()) {
            let collapsed = p.split_whitespace().collect::<Vec<_>>().join(" ");
            let invocation = if interactive {
                "prompt".to_string()
            } else {
                std::env::var("OCODEX_INVOCATION").unwrap_or_else(|_| "exec_json".to_string())
            };
            match invocation.as_str() {
