+fn tokenize(s: &str) -> Vec<String> {
+    s.split(|c: char| !c.is_alphanumeric())
+        .filter(|t| !t.is_empty())
+        .map(|t| t.to_lowercase())
+        .collect()
+}
+
+fn score_text(query: &str, title: &str, body: &str) -> (u32, Option<String>) {
+    let q_tokens = tokenize(query);
+    let mut score = 0u32;
+    let title_l = title.to_lowercase();
+    for t in &q_tokens {
+        if title_l.contains(t) {
+            score += 2;
+        }
+    }
+    let body_l = body.to_lowercase();
+    let mut first_hit: Option<String> = None;
+    for t in &q_tokens {
+        if body_l.contains(t) {
+            score += 1;
+            if first_hit.is_none() {
+                // Try to extract a short snippet around the first occurrence
+                if let Some(idx) = body_l.find(t) {
+                    let start = idx.saturating_sub(80);
+                    let end = (idx + t.len() + 80).min(body.len());
+                    first_hit = Some(body[start..end].to_string());
+                }
+            }
+        }
+    }
+    (score, first_hit)
+}
