+async fn fetch_page_text(client: &reqwest::Client, url: &Url, max_bytes: usize) -> anyhow::Result<(String, String)> {
+    let resp = client.get(url.clone()).send().await?;
+    let status = resp.status();
+    let ct = resp
+        .headers()
+        .get(reqwest::header::CONTENT_TYPE)
+        .and_then(|v| v.to_str().ok())
+        .unwrap_or("")
+        .to_lowercase();
+    if !status.is_success() {
+        anyhow::bail!("non-200 status {}", status);
+    }
+    if !(ct.starts_with("text/") || ct.contains("html") || ct.contains("xml")) {
+        anyhow::bail!("unsupported content-type: {}", ct);
+    }
+    let mut bytes = Vec::with_capacity(2048);
+    let mut stream = resp.bytes_stream();
+    use futures::StreamExt as _;
+    while let Some(chunk) = stream.next().await {
+        let chunk = chunk?;
+        if bytes.len() + chunk.len() > max_bytes {
+            let remaining = max_bytes.saturating_sub(bytes.len());
+            bytes.extend_from_slice(&chunk[..remaining.min(chunk.len())]);
+            break;
+        } else {
+            bytes.extend_from_slice(&chunk);
+        }
+    }
+    let text = String::from_utf8_lossy(&bytes).to_string();
+    let document = scraper::Html::parse_document(&text);
+    let title_sel = scraper::Selector::parse("title").unwrap();
+    let title = document
+        .select(&title_sel)
+        .next()
+        .and_then(|t| t.text().next())
+        .unwrap_or("")
+        .trim()
+        .to_string();
+    let mut body_text = String::new();
+    // Prefer p-tags to keep it compact
+    let p_sel = scraper::Selector::parse("p").unwrap();
+    for p in document.select(&p_sel).take(200) {
+        let t = p.text().collect::<String>();
+        if !t.trim().is_empty() {
+            body_text.push_str(t.trim());
+            body_text.push('\n');
+        }
+        if body_text.len() > max_bytes {
+            break;
+        }
+    }
+    if body_text.is_empty() {
+        body_text = text;
+    }
+    Ok((title, body_text))
+}
