+    // Seed with initial results from web_search provider selection
+    // Reuse internal provider choice + DDG fallback
+    enum Provider { Serp(String), Cse(String, String), Ddg }
+    let provider = {
+        if let Ok(k) = std::env::var("SERPAPI_KEY") && !k.trim().is_empty() { Provider::Serp(k) }
+        else if let (Ok(k), Ok(cx)) = (std::env::var("GOOGLE_CSE_KEY"), std::env::var("GOOGLE_CSE_CX"))
+            && !k.trim().is_empty() && !cx.trim().is_empty() { Provider::Cse(k, cx) }
+        else { Provider::Ddg }
+    };
+
+    let mut seeds: Vec<SearchResultItem> = match provider {
+        Provider::Serp(ref key) => serpapi_search(&client, key, &q, cap_results).await,
+        Provider::Cse(ref key, ref cx) => google_cse_search(&client, key, cx, &q, cap_results).await,
+        Provider::Ddg => {
