+    let mut visited: HashSet<String> = HashSet::new();
+    let mut findings: Vec<WebResearchFinding> = Vec::new();
+    let mut best: Option<WebResearchFinding> = None;
+    let mut pages_fetched = 0usize;
+    let mut queue: Vec<(u32, String)> = Vec::new();
+    for s in &seeds { queue.push((0, s.url.clone())); }
+
+    while let Some((depth, url)) = queue.pop() {
+        if pages_fetched >= max_pages { break; }
+        if !visited.insert(url.clone()) { continue; }
+        pages_fetched += 1;
