+                if pages_fetched + queue.len() >= max_pages { break; }
+                queue.insert(0, (depth + 1, next.into_string()));
+            }
+        }
+    }
+
+    let stopped_reason = if pages_fetched >= max_pages {
+        format!("reached max_pages {}", max_pages)
+    } else {
+        format!("queue exhausted at depth {}", max_depth)
+    };
+
+    let res = WebResearchResult { query: q, best, findings, visited: pages_fetched, stopped_reason };
