fn tokenize(s: &str) -> Vec<String> {
    s.split(|c: char| !c.is_alphanumeric())
        .filter(|t| !t.is_empty())
        .map(|t| t.to_lowercase())
        .collect()
}

fn score_text(query: &str, title: &str, body: &str) -> (u32, Option<String>) {
    let q_tokens = tokenize(query);
    let mut score = 0u32;
    let title_l = title.to_lowercase();
    for t in &q_tokens {
        if title_l.contains(t) {
            score += 2;
        }
    }
    let body_l = body.to_lowercase();
    let mut first_hit: Option<String> = None;
    for t in &q_tokens {
        if body_l.contains(t) {
            score += 1;
            if first_hit.is_none() {
                // Try to extract a short snippet around the first occurrence
                if let Some(idx) = body_l.find(t) {
                    let start = idx.saturating_sub(80);
                    let end = (idx + t.len() + 80).min(body.len());
                    first_hit = Some(body[start..end].to_string());
                }
            }
        }
    }
    (score, first_hit)
}

fn extract_links(html: &str, base: &Url, q: &str, cap: usize) -> Vec<Url> {
    let mut out = Vec::new();
    let document = scraper::Html::parse_document(html);
    let selector = scraper::Selector::parse("a[href]").unwrap();
    let q_tokens = tokenize(q);
    for el in document.select(&selector) {
        if let Some(href) = el.value().attr("href") {
            if let Ok(abs) = base.join(href) {
                // prefer links whose text contains query tokens
                let text = el.text().collect::<String>().to_lowercase();
                let mut rel = 0u32;
                for t in &q_tokens {
                    if text.contains(t) {
                        rel += 1;
                    }
                }
                if rel > 0 {
                    out.push((rel, abs));
                }
            }
        }
        if out.len() >= cap * 3 {
            break;
        }
    }
    // sort by relevance desc and dedup by URL
    out.sort_by(|a, b| b.0.cmp(&a.0));
    let mut seen = HashSet::new();
    let mut urls = Vec::new();
    for (_, u) in out {
        if seen.insert(u.as_str().to_string()) {
            urls.push(u);
            if urls.len() >= cap {
                break;
            }
        }
    }
    urls
}

async fn fetch_page_text(
    client: &reqwest::Client,
    url: &Url,
    max_bytes: usize,
) -> anyhow::Result<(String, String)> {
    let resp = client.get(url.clone()).send().await?;
    let status = resp.status();
    let ct = resp
        .headers()
        .get(reqwest::header::CONTENT_TYPE)
        .and_then(|v| v.to_str().ok())
        .unwrap_or("")
        .to_lowercase();
    if !status.is_success() {
        anyhow::bail!("non-200 status {}", status);
    }
    if !(ct.starts_with("text/") || ct.contains("html") || ct.contains("xml")) {
        anyhow::bail!("unsupported content-type: {}", ct);
    }
    let mut bytes = Vec::with_capacity(2048);
    let mut stream = resp.bytes_stream();
    use futures::StreamExt as _;
    while let Some(chunk) = stream.next().await {
        let chunk = chunk?;
        if bytes.len() + chunk.len() > max_bytes {
            let remaining = max_bytes.saturating_sub(bytes.len());
            bytes.extend_from_slice(&chunk[..remaining.min(chunk.len())]);
            break;
        } else {
            bytes.extend_from_slice(&chunk);
        }
    }
    let text = String::from_utf8_lossy(&bytes).to_string();
    let document = scraper::Html::parse_document(&text);
    let title_sel = scraper::Selector::parse("title").unwrap();
    let title = document
        .select(&title_sel)
        .next()
        .and_then(|t| t.text().next())
        .unwrap_or("")
        .trim()
        .to_string();
    let mut body_text = String::new();
    // Prefer p-tags to keep it compact
    let p_sel = scraper::Selector::parse("p").unwrap();
    for p in document.select(&p_sel).take(200) {
        let t = p.text().collect::<String>();
        if !t.trim().is_empty() {
            body_text.push_str(t.trim());
            body_text.push('\n');
        }
        if body_text.len() > max_bytes {
            break;
        }
    }
    if body_text.is_empty() {
        body_text = text;
    }
    Ok((title, body_text))
}

