async fn serpapi_search(
    client: &reqwest::Client,
    key: &str,
    query: &str,
    cap: usize,
) -> Vec<SearchResultItem> {
    let url = format!(
        "https://serpapi.com/search.json?engine=google&q={}&num={}&api_key={}",
        urlencoding::encode(query),
        cap,
        urlencoding::encode(key)
    );
    let mut out = Vec::new();
    let Ok(resp) = client.get(url).send().await else {
        return out;
    };
    let Ok(text) = resp.text().await else {
        return out;
    };
    let v: serde_json::Value = serde_json::from_str(&text).unwrap_or(json!({}));
    if let Some(items) = v.get("organic_results").and_then(|x| x.as_array()) {
        for item in items.iter().take(cap) {
            let title = item
                .get("title")
                .and_then(|x| x.as_str())
                .unwrap_or("")
                .to_string();
            let url = item
                .get("link")
                .and_then(|x| x.as_str())
                .unwrap_or("")
                .to_string();
            let snippet = item
                .get("snippet")
                .and_then(|x| x.as_str())
                .unwrap_or(title.as_str())
                .to_string();
            if !title.is_empty() && !url.is_empty() {
                out.push(SearchResultItem { title, url, snippet });
            }
        }
    }
    out
}

