        if let Ok(k) = std::env::var("SERPAPI_KEY")
            && !k.trim().is_empty()
        {
            Provider::Serp(k)
        } else if let (Ok(k), Ok(cx)) = (
            std::env::var("GOOGLE_CSE_KEY"),
            std::env::var("GOOGLE_CSE_CX"),
        ) && !k.trim().is_empty()
            && !cx.trim().is_empty()
        {
            Provider::Cse(k, cx)
        } else {
            Provider::Ddg
        }
    };

